operation.add:
$(document).on('click', '#button-1click', function() {
    $.post("index.php?route=product/product/process1Click", function (data) {
        if(data.hasOwnProperty('success') && data.success == true){
            console.log(data.message);
        }
    })
});

public function process1Click(){
    $result['success'] = true;
    $result['message'] = 'Ваша заявка успешно отправлена на почту';

    $mail = new Mail();
    $mail->protocol = $this->config->get('config_mail_protocol');
    $mail->parameter = $this->config->get('config_mail_parameter');
    $mail->smtp_hostname = $this->config->get('config_mail_smtp_hostname');
    $mail->smtp_username = $this->config->get('config_mail_smtp_username');
    $mail->smtp_password = html_entity_decode($this->config->get('config_mail_smtp_password'), ENT_QUOTES, 'UTF-8');
    $mail->smtp_port = $this->config->get('config_mail_smtp_port');
    $mail->smtp_timeout = $this->config->get('config_mail_smtp_timeout');

    $mail->setTo('l.aleksei.minsk@gmail.com');
    $mail->setFrom('opencart@gmail.com');
    $mail->setSender(html_entity_decode($this->config->get('config_name'), ENT_QUOTES, 'UTF-8'));
    $mail->setSubject(html_entity_decode($this->language->get('text_new_customer'), ENT_QUOTES, 'UTF-8'));
    $mail->setText($result['message']);
    $mail->send();

    $this->response->addHeader('Content-Type: application/json');
    $this->response->setOutput(json_encode($result));
}
